 AOS.init();
/*Start My Cart*/
$('.cart').on('click', function () {
  $('.mycart .overlay').fadeIn();
  $('.mycart .thecart').css('transform', 'translateX(0px)');
  $('.mycart .thecart').css('cursor', 'default');
  $("body").css('cursor','url(data:image/svg+xml;utf8;base64,),auto');
});

$('.mycart .close, body, html').on('click', function () {
  $('.mycart .overlay').fadeOut();
  $('.mycart .thecart').css('transform', 'translateX(-100px)');
  $('body').css('cursor', 'default');
});

$('.mycart .thecart, .cart').on('click', function (e) {
  if ($('.mycart .overlay:hidden')) {
    e.stopPropagation();
  }
});

$('a[href="#"]').click(function (e) {
  e.preventDefault();
});

